mapper.select:
SELECT

FROM t_employee t


        t.state=#{state}


        AND t.position_id=#{positionId}

SELECT t.id, t.id user_id, t1.user_name, t1.email, t.`name`, t.sex, t.card_type, t.card_no, t.mobile, t.address, t.state, t.position_id, t2.`name` position_name, t2.dept_id, t3.`name` dept_name FROM `t_employee` t INNER JOIN t_user t1 ON t.user_id = t1.id INNER JOIN t_position t2 ON t.position_id = t2.id INNER JOIN t_department t3 ON t2.dept_id = t3.id
t.state = #{state}
AND t.user_id = #{userId}
AND t1.user_name = #{userName}
AND t.`name` = #{name}
AND t.mobile = #{mobile}
AND t2.dept_id = #{deptId}
AND t.position_id = #{positionId}
